class PostComments{constructor(e){this.postId=e,this.postContainer=$("#post-"+e),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each((function(){t.deleteComment($(this))}))}createComment(e){let t=this;this.newCommentForm.submit((function(n){n.preventDefault();$.ajax({type:"post",url:"/comments/create",data:$(this).serialize(),success:function(n){let o=t.newCommentDom(n.data);$("#post-comments-"+e).prepend(o),t.deleteComment($(" .delete-comment-button",o))},error:function(e){console.log(e.responseText)}})}))}newCommentDom(e){return $(`<li id="comment-${e.comment._id}" class=" new-comment-section">\n                        <p>\n\n                            <small>\n                                <a class="delete-comment-button" href="/comments/destroy/${e.comment._id}">\n                                <button name="Delete">X</button>\n                                </a>\n                            </small>\n                            <span class="comment-content">\n                            ${e.comment.content}\n                            </span>\n                            <br>\n                            <small>\n                                ${e.username}\n                            </small>\n                        </p>\n\n                </li>`)}deleteComment(e){$(e).click((function(t){t.preventDefault(),$.ajax({type:"GET",url:$(e).prop("href"),success:function(e){$("#comment-"+e.data.comment_id).remove()},error:function(e){console.log(e.responseText)}})}))}}